        let countdown = null;
        let timeLeft = 10;
        let originalTime = 10;
        let isRunning = false;
        
        const timeInput = document.getElementById('timeInput');
        const countdownDisplay = document.getElementById('countdown');
        const startBtn = document.getElementById('startBtn');
        const resetBtn = document.getElementById('resetBtn');
        
        timeInput.addEventListener('input', function() {
            if (!isRunning) {
                const newTime = parseInt(this.value) || 10;
                if (newTime < 1) {
                    this.value = 1;
                    timeLeft = 1;
                } else if (newTime > 3600) {
                    this.value = 3600;
                    timeLeft = 3600;
                } else {
                    timeLeft = newTime;
                }
                originalTime = timeLeft;
                updateDisplay();
            }
        });
        
        startBtn.addEventListener('click', function() {
            if (!isRunning) {
                startCountdown();
            }
        });
        
        resetBtn.addEventListener('click', function() {
            resetCountdown();
        });

        function startCountdown() {
            isRunning = true;
            startBtn.disabled = true;
            timeInput.disabled = true;
            
            countdown = setInterval(function() {
                timeLeft--;
                updateDisplay();
                
                if (timeLeft <= 0) {
                    clearInterval(countdown);
                    countdownDisplay.textContent = 'Süre doldu!';
                    countdownDisplay.classList.add('finished');
                    isRunning = false;
                    startBtn.disabled = false;
                    timeInput.disabled = false;
                    startBtn.textContent = '🚀 Başlat';

                    try {
                        const audio = new Audio('data:audio/wav;base64,');
                        audio.play();
                    } catch (e) {

                    }
                }
            }, 1000);
            
            startBtn.textContent = '⏸️ Çalışıyor...';
        }
        
        function resetCountdown() {
            clearInterval(countdown);
            isRunning = false;
            timeLeft = originalTime;
            startBtn.disabled = false;
            timeInput.disabled = false;
            startBtn.textContent = '🚀 Başlat';
            countdownDisplay.classList.remove('finished', 'warning');
            updateDisplay();
        }
        
        function updateDisplay() {
            countdownDisplay.textContent = timeLeft;
            
            // Son 5 saniyede uyarı rengi
            if (timeLeft <= 5 && timeLeft > 0 && isRunning) {
                countdownDisplay.classList.add('warning');
            } else {
                countdownDisplay.classList.remove('warning');
            }
        }
        
        window.addEventListener('load', function() {
            updateDisplay();
        });

        timeInput.addEventListener('keypress', function(e) {
            if (e.key === 'Enter' && !isRunning) {
                startCountdown();
            }
        });
